      document.querySelector("body").style.overflow = "hidden";
      gsap.to(this.$refs.line_2, { attr: { x1: 20 } });
      gsap.to(this.$refs.line_1, { delay:0.15,rotate:45, attr: { x2: 57 } });
      gsap.to(this.$refs.line_2, { delay:0.2, autoAlpha: 0 });
      gsap.to(this.$refs.line_3, { delay:0.15, rotate:-45, attr: { x2: 57 } });
    },
    burgerToStack(){
      document.querySelector("body").style.overflow = "initial";
      gsap.to(this.$refs.line_1, { rotate:0, attr: { x2: 55 } });
      gsap.to(this.$refs.line_2, { autoAlpha: 1 });
      gsap.to(this.$refs.line_3, { rotate:0, attr: { x2: 55 } });
      gsap.to(this.$refs.line_2, { attr: { x1: 38 } });

    },
    openFilters(){
      this.app.classList.toggle("filtersOpen");
    },
  },
  watch:{
    menuState: {
      handler(menuState){
        menuState && this.burgerToCross();
        !menuState && this.burgerToStack();
      }
    },
    $route: {
      handler(){
        this.setMenue(false);
        if(window.location.pathname != "/") this.isHome = false;
      }
    },
    filtersState: {
      handler(){
        this.openFilters();
      }
    }
  }
};

</script>

